import { useEffect, useState } from "react";
import Input from "./components/Input";
import TEst from "./components/test";

function App() {
  const [nom, setNom] =useState("");
  const [prenom, setPrenom] =useState("");
  const [age, setAge] =useState(0);
  const handleChange = (e) => {
    switch(e.target.name){
      case 'nom' : setNom(e.target.value);break;
      case 'prenom' : setPrenom(e.target.value);break;
      default : setAge(e.target.value);
    }
  }
  const handleSubmit = (e) => {
    fetch("192.168.43.5:5000/register", {
      method: "POST",
      body: JSON.stringify({nom, prenom, age}),
      headers : {
        "Content-type" : "application/json"
      }
    })
    .then(data => data.json())
    .catch(() =>{
      console.log('error')
    })
  }
  console.log(nom)
  return (
    <div>
        <Input type="text" name="nom" style={{width: "100%", height: "30px", marginBottom: "20px",  borderRadius: "5px"}} value={nom} handleChange={handleChange} />
        <Input type="text" name="prenom" style={{width: "100%", height: "30px", marginBottom: "20px",  borderRadius: "5px"}} value={prenom} handleChange={handleChange} />
        <Input type="number" name="age" style={{width: "100%", height: "30px", marginBottom: "20px",  borderRadius: "5px"}} value={age} handleChange={handleChange} />
        <Input type="button" name="btn" style={{width: "100%",height:"30px",fontSize: "20px",color: "white",backgroundColor: "dodgerblue", border: "1px solid dodgerblue", borderRadius: "5px", marginTop: "20px"}} value={"submit"} handleSubmit = {handleSubmit} handleChange={handleSubmit} />
        {JSON.stringify({nom, prenom, age})}
    </div>
  );
}

export default App;
