import { useState } from "react"


const Formulaire = (props) => {
    const [fields, setFields] = useState({
        nom : "",
        prenom : "",
        age : 0,
        homme : false,
        femme : true,
        check : true
    })

    const handleChange = e => {
        setFields({
            ...fields,
            [e.target.name] : e.target.type === "checkbox" ? e.target.checked : e.target.value
        })
    }

    const style = {
        width : "100%"
    }

     return (
        <>
            <input style={style} type="text" name="nom" value = {fields.nom} onChange={handleChange} />
            <input style={style} type="text" name="prenom" value = {fields.prenom} onChange={handleChange} />
            <input style={style} type="text" name="age" value = {fields.age} onChange={handleChange} />
            <input style={style} type="radio" id="homme" name="homme" checked = {fields.homme} onClick={(e) =>setFields({...fields, homme : true, femme : false})} />
            <input style={style} type="radio" id="femme" name="femme" checked = {fields.femme} onClick={(e) =>setFields({...fields, homme : false, femme : true})} />
            <input style={style} type="checkbox" name="check" checked = {fields.check} onChange={handleChange} />
            <input style={style} type={"button"} value="valider"></input>
            {JSON.stringify(fields)}
        </>
    )
}

export default Formulaire
