function App() {
  const [compt, setCompt] = useState(true)
  return (
    <>
      {compt ? <p style={{fontSize:"100px"}}>&#128540;</p> : <p style={{fontSize:"100px"}}>&#128540;</p>}
      <button onClick={() => setCompt(v =>!v)}></button>
    </>
  );
}

export default App;
