import { useState } from "react";

function App() {
  const [compt, setCompt] = useState(true)
  return (
    <div style={{marginLeft : "20%",textAlign: "center", marginRight : '20%', width : "60%", border : "1px solid red"}}>
      <p style={{ fontSize: "200px" }}>{compt ? <p>&#128540;</p> : <p>&#128545;</p>}</p>
      <p style={{color : "red"}}>click vite le button</p>
      <button onClick={() => setCompt(v => !v)}></button>
    </div>
  );
}

export default App;
