import { useState } from "react"


const Formulaire = (props) => {
    const [fields, setFields] = useState({
        nom : "",
        prenom : "",
        age : 0,
        homme : false,
        femme : true,
        check : true
    })

    const handleChange = e => {
        setFields({
            ...fields,
            [e.target.name] : e.target.type === "checkbox" ? e.target.checked : e.target.value
        })
    }

    const style = {
        width : "100%",
        radioDiv : {
            display : "flex",
            flexDirection : "row",
            justifyContent : "space-between"
        }
    }

     return (
        <>
            <input style={style} type="text" name="nom" value = {fields.nom} onChange={handleChange} />
            <input style={style} type="text" name="prenom" value = {fields.prenom} onChange={handleChange} />
            <input style={style} type="text" name="age" value = {fields.age} onChange={handleChange} />
            <div style={style.radioDiv}>
            <div>
            <input style={{}} type="radio" id="homme" name="homme" defaultChecked = {fields.homme} onClick={(e) =>setFields({...fields, homme : true, femme : false})} />homme
            </div>
            <div>
            <input style={{}} type="radio" id="femme" name="femme" defaultChecked = {fields.femme} onClick={(e) =>setFields({...fields, homme : false, femme : true})} />femme
            </div>
            </div>
            <input style={{}} type="checkbox" name="check" checked = {fields.check} onChange={handleChange} />j'accepte les conditions
            <input style={style} type={"button"} value="valider"></input>
            {JSON.stringify(fields)}
        </>
    )
}

export default Formulaire
