import { useState } from "react"


const Formulaire = (props) => {

    const [fields, setFields] = useState({
        nom: "",
        prenom: "",
        age: 20,
        homme: false,
        femme: true,
        check: true
    })

    const handleChange = e => {
        setFields({
            ...fields,
            [e.target.name]: e.target.type === "checkbox" ?
                e.target.checked : e.target.value
        })
    }

    const style = {
        radioDiv: {
            display: "flex",
            flexDirection: "row",
            justifyContent: "space-between",
            marginBottom: '15px'
        },
        inputText: {
            width: "100%",
            height: "30px",
            borderRadius: "5px",
            border: "1px solid gray",
            marginBottom: "15px"
        },
        radio: {},
        btnValider: {
            width: '100%',
            height: 30,
            fontSize: 18,
            color: "white",
            border: "1px solid dodgerblue",
            borderRadius: 5,
            backgroundColor: "dodgerblue",
            cursor: "pointer"
        },
        confident: {
            marginBottom: 15
        }
    }
    const handleSubmit = e => {
        if (fields.nom === "" ||
            fields.prenom === "" ||
            fields.age < 1) {
        } else {
            props.getPersonne(fields)
            setFields({
                nom: "",
                prenom: "",
                age: 20,
                homme: false,
                femme: true,
                check: true
            })
        }
    }

    return (
        <>
            <input
                style={style.inputText}
                type="text"
                name="nom"
                value={fields.nom}
                onChange={handleChange} />
            <input
                style={style.inputText}
                type="text"
                name="prenom"
                value={fields.prenom}
                onChange={handleChange} />
            <input
                style={style.inputText}
                type="number"
                name="age"
                value={fields.age}
                onChange={handleChange} />
            <div
                style={style.radioDiv}>
                <div>
                    <input
                        style={{}}
                        type="radio"
                        id="homme"
                        name="homme"
                        checked={fields.homme}
                        onClick={(e) => setFields({
                            ...fields,
                            homme: true,
                            femme: false
                        })} />homme
                </div>
                <div>
                    <input
                        style={{}}
                        type="radio"
                        id="femme"
                        name="femme"
                        checked={fields.femme}
                        onClick={(e) => setFields({
                            ...fields,
                            homme: false,
                            femme: true
                        })} />femme
                </div>
            </div>
            <input
                style={style.confident}
                type="checkbox"
                name="check"
                checked={fields.check}
                onChange={handleChange} />j'accepte les conditions de confidentialit√©
            <input
                style={style.btnValider}
                type={"button"}
                disabled={fields.nom === "" || fields.prenom === "" || fields.age < 1}
                value="valider"
                onClick={handleSubmit}></input>
            {JSON.stringify(fields)}
        </>
    )
}

export default Formulaire
