import { useState } from "react"


const Formulaire = (props) => {
    const [fields, setFields] = useState({
        nom : "",
        prenom : "",
        age : 0,
        homme : false,
        femme : !this.homme,
        check : true
    })

    const handleChange = e => {
        setFields({
            ...fields,
            [e.target.name] : e.target.type === "checkbox" ? e.target.checked : e.target.value
        })
    }

     return (
        <>
            <input type="text" name="nom" value = {fields.nom} onChange={handleChange} />
            <input type="text" name="prenom" value = {fields.prenom} onChange={handleChange} />
            <input type="text" name="age" value = {fields.age} onChange={handleChange} />
            <input type="radio" name="homme" checked = {fields.homme} onChange={handleChange} />
            <input type="radio" name="femme" checked = {fields.femme} onChange={handleChange} />
            <input type="checkbox" name="check" checked = {fields.check} onChange={handleChange} />
            <input type={"button"} value="valider"></input>
            {JSON.stringify(fields)}
        </>
    )
}

export default Formulaire
