import { useState } from "react";

function App() {
  const [compt, setCompt] = useState(true)
  return (
    <div style={{marginLeft : "20%", marginRight : '20%', width : "60%", border : "1px solid red"}}>
      <p style={{ fontSize: "100px" }}>{compt ? "&#128540;" : "&#128545;"}</p> : <p style={{ fontSize: "100px" }}></p>
      <button onClick={() => setCompt(v => !v)}></button>
    </div>
  );
}

export default App;
