import { useState } from "react";
import Input from "./components/Input";

function App() {
  setInterval(() => {
    setFields({
      nom: "tiemeni",
      prenom : "christian",
      age : 22
    })
  }, 3000);
  const [fields, setFields] =useState({
    nom : "",
    prenom : "",
    age : 0
  });
  const handleChange = (e) => {
    const nom_ = e.target.name
    setFields({...fields, [nom_] : e.target.value ? setFields({[nom_] : ""}) : e.target.value});
  }
  const handleSubmit = (e) => {
    fetch("http://192.168.43.5:5000/register", {
      method: "POST",
      body: JSON.stringify(fields),
      headers : {
        "Content-type" : "application/json"
      }
    })
    .then(data => data.json())
    .then(res => console.log(res))
    .catch(() =>{
      console.log('error')
    })
  }

  const style = {
    width: "100%", 
    height: "30px", 
    marginBottom: "20px",  
    borderRadius: "5px"
  }

  return (
    <div>
        <Input type="text" name="nom" style={{...style}} value={fields.nom} handleChange={handleChange} />
        <Input type="text" name="prenom" style={{...style}} value={fields.prenom} handleChange={handleChange} />
        <Input type="number" name="age" style={{...style}} value={fields.age} handleChange={handleChange} />
        <Input type="button" name="btn" style={{...style,height:"30px",fontSize: "20px",color: "white",backgroundColor: "dodgerblue", border: "1px solid dodgerblue", marginTop: "20px"}} value={"submit"} handleSubmit = {handleSubmit} handleChange={handleSubmit} />
        {JSON.stringify({nom : fields.nom, prenom : fields.prenom, age : fields.age})}
    </div>
  );
}

export default App;
